def server(input, output, session):
    @render.ui
    def selected_season_alt_cast_elements() -> ui.TagList:
        selected_img_paths = selected_season().get_element_imgs()
        return ui_imgs(selected_img_paths, width="50px")

    @render.ui
    def selected_season_op_characters() -> ui.TagList:
        selected_img_paths = selected_season().get_op_character_imgs()
        return ui_imgs(selected_img_paths, width="50px")

    @render.ui
    def selected_season_special_invites() -> ui.TagList:
        selected_img_paths = selected_season().get_special_invite_imgs()
        return ui_imgs(selected_img_paths, width="50px")

    @reactive.calc
    def selected_season() -> Season:
        return Season(input.selected_season())

    @render.download(
        filename=(
            "character_inventory_"
            + datetime.today().date().strftime("%Y_%m_%d")
            + ".json"
        )
    )
    def export_inventory():
        yield json.dumps(character_inventory()).encode("utf-8")

    @reactive.calc
    def character_inventory() -> list[str]:
        inventory = list(input.character_inventory())
        if input.include_traveler():
            inventory += ["Traveler"]
        return inventory

    @reactive.effect()
    def import_inventory():
        file = input.import_inventory()
        if file is not None:
            with open(file[0]["datapath"], "r") as f:
                inventory = json.load(f)
            ui_update_inventory(ui, inventory)

    @render.text
    def difficulty_text() -> str:
        season = selected_season()
        date = season.date.strftime("%B %Y")
        count = season.count_elig_characters_in(character_inventory())
        difficulty = season.highest_tier(count)
        if difficulty == "None":
            return f"You do not have enough characters to participate in the {date} season."
        else:
            return f"The highest difficulty you can challenge for the {date} season is {difficulty}."

    @render.text
    def eligible_characters_text() -> str:
        count = selected_season().count_elig_characters_in(character_inventory())
        return f"You have {count} eligible characters for this season:"

    @render.ui
    def eligible_characters_imgs() -> ui.TagList:
        selected_imgs = selected_season().get_elig_character_imgs_in(
            character_inventory(), input.traveler_name()
        )
        return ui_imgs(selected_imgs, width="50px")


app = App(app_ui, server)

import pandas as pd
from pathlib import Path
from datetime import datetime, date

GI_WIKI_IMG_PATH = "https://static.wikia.nocookie.net/gensin-impact/images"


def file_path(path: str) -> str:
    return Path(__file__).parent / path


def characters() -> pd.DataFrame:
    return pd.read_csv(file_path("characters.csv"))


def character_names() -> list[str]:
    return characters()["character"].tolist()


def character_keys() -> dict:
    return {n.replace(" ", ""): n for n in character_names()}


def element_img_paths() -> dict:
    return {
        "Anemo": f"{GI_WIKI_IMG_PATH}/1/10/Element_Anemo.svg",
        "Cryo": f"{GI_WIKI_IMG_PATH}/7/72/Element_Cryo.svg",
        "Dendro": f"{GI_WIKI_IMG_PATH}/7/73/Element_Dendro.svg",
        "Electro": f"{GI_WIKI_IMG_PATH}/f/ff/Element_Electro.svg",
        "Geo": f"{GI_WIKI_IMG_PATH}/9/9b/Element_Geo.svg",
        "Hydro": f"{GI_WIKI_IMG_PATH}/8/80/Element_Hydro.svg",
        "Pyro": f"{GI_WIKI_IMG_PATH}/2/2c/Element_Pyro.svg",
    }


def character_img_paths() -> dict:
    character_df = characters()
    names = character_df["character"].tolist()
    img_paths = (GI_WIKI_IMG_PATH + character_df["img_path"]).tolist()
    return {n: p for n, p in zip(names, img_paths)}


def traveler_img_path(player_choice: str) -> str:
    if player_choice == "Aether":
        return f"{GI_WIKI_IMG_PATH}/a/a5/Aether_Icon.png"
    elif player_choice == "Lumine":
        return f"{GI_WIKI_IMG_PATH}/9/9c/Lumine_Icon.png"


def season_labels() -> list[str]:
    season_dates = seasons()["date"].tolist()
    return [datetime.strftime(d, "%B %Y") for d in season_dates]


def seasons() -> list[date]:
    seasons = pd.read_csv(file_path("seasons.csv"), dtype=str)
    seasons["date"] = [datetime.strptime(d, "%Y-%m-%d").date() for d in seasons["date"]]
    return seasons

from datetime import datetime
import load_data
import pandas as pd


class Season:
    def __init__(self, date_label: str):
        self.date = datetime.strptime(date_label, "%B %Y").date()
        seasons = load_data.seasons()
        selected_season = seasons[seasons["date"] == self.date].melt(
            id_vars=["date"], var_name="field"
        )
        self.alt_cast_elements = Season._pull_alt_cast_elements_from(selected_season)
        self.special_invites = Season._pull_special_invites_from(selected_season)
        self.op_characters = Season._pull_op_characters_from(selected_season)

    def _pull_alt_cast_elements_from(df: pd.DataFrame) -> list[str]:
        return Season._df_field_to_list(df, "alt_cast_element")

    def _pull_special_invites_from(df: pd.DataFrame) -> list[str]:
        return Season._df_field_to_list(df, "special_invite")

    def _pull_op_characters_from(df: pd.DataFrame) -> list[str]:
        return Season._df_field_to_list(df, "op_character")

    def _df_field_to_list(df: pd.DataFrame, field_name: str) -> list[str]:
        return df[df["field"].str.contains(field_name)]["value"].tolist()

    def get_element_imgs(self) -> tuple[str, str]:
        img_paths = load_data.element_img_paths()
        selected_paths = [img_paths[e] for e in self.alt_cast_elements]
        return zip(self.alt_cast_elements, selected_paths)

    def get_op_character_imgs(self) -> tuple[str, str]:
        img_paths = load_data.character_img_paths()
        selected_paths = [img_paths[c] for c in self.op_characters]
        return zip(self.op_characters, selected_paths)

    def get_special_invite_imgs(self) -> tuple[str, str]:
        img_paths = load_data.character_img_paths()
        selected_paths = [img_paths[c] for c in self.special_invites]
        return zip(self.special_invites, selected_paths)

    def count_elig_characters_in(self, character_inventory: list[str]) -> int:
        return len(self.get_elig_characters_in(character_inventory))

    def get_elig_characters_in(self, inventory: list[str]) -> list[str]:
        modified_inventory = inventory + [
            c for c in self.op_characters if c not in inventory
        ]

        characters = load_data.characters()
        available_characters = characters[
            characters["character"].isin(modified_inventory)
        ]

        eligible_characters = available_characters[
            available_characters["element"].isin(self.alt_cast_elements)
            | available_characters["character"].isin(self.special_invites)
        ]["character"].tolist()

        if "Traveler" in inventory:
            eligible_characters += ["Traveler"]

        return eligible_characters

    def get_elig_character_imgs_in(
        self, inventory: list[str], traveler_name: str
    ) -> tuple[str, str]:
        img_paths = load_data.character_img_paths()
        selected_characters = self.get_elig_characters_in(inventory)
        selected_img_names = [c for c in selected_characters if c != "Traveler"]
        selected_img_paths = [img_paths[n] for n in selected_img_names]

        if "Traveler" in inventory:
            selected_img_names += [traveler_name]
            selected_img_paths += [load_data.traveler_img_path(traveler_name)]

        return zip(selected_img_names, selected_img_paths)

    def highest_tier(self, n_chars: int) -> str:
        if self.date < datetime(2024, 9, 1).date():
            tier_counts = [10, 14, 18]
        else:
            tier_counts = [8, 12, 16, 22]

        tier_names = ["Easy", "Normal", "Hard", "Visionary"][0 : len(tier_counts)]
        tier_mask = [n_chars >= i for i in tier_counts]
        valid_tiers = [name for name, include in zip(tier_names, tier_mask) if include]
        if len(valid_tiers) > 0:
            return valid_tiers[-1]
        else:
            return "None"

from shiny import ui
import load_data


def ui_imgs(names_and_paths: tuple[str, str], width: str) -> ui.TagList:
    imgs = [
        ui.tooltip(ui.img(src=p, width=width), n, placement="bottom")
        for n, p in names_and_paths
    ]
    return ui.TagList(*imgs)


def ui_update_inventory(app_ui: ui, inventory: dict | list[str]):
    if "characters" in inventory:
        inventory = _convert_GOOD_to_list(inventory)

    traveler_in_inv = "Traveler" in inventory
    app_ui.update_checkbox(id="include_traveler", value=traveler_in_inv)
    if traveler_in_inv:
        inventory.remove("Traveler")
    app_ui.update_selectize(id="character_inventory", selected=inventory)


def _convert_GOOD_to_list(inventory_good: dict) -> list[str]:
    keys = [c["key"] for c in inventory_good["characters"] if c["level"] >= 70]
    inventory_list = [
        load_data.character_keys()[k] for k in keys if "Traveler" not in k
    ]

    traveler_in_inv = any(["Traveler" in k for k in keys])
    if traveler_in_inv:
        inventory_list += ["Traveler"]

    return inventory_list

from ui_elements import _convert_GOOD_to_list


def test_convert_GOOD_to_list():
    test_json = {"characters": [{"key": "TravelerGeo", "level": 70}]}
    assert _convert_GOOD_to_list(test_json) == ["Traveler"]

    test_json = {"characters": [{"key": "TravelerElectro", "level": 69}]}
    assert _convert_GOOD_to_list(test_json) == []

    test_json = {
        "characters": [
            {"key": "TravelerDendro", "level": 70},
            {"key": "TravelerAnemo", "level": 70},
        ]
    }
    assert _convert_GOOD_to_list(test_json) == ["Traveler"]

    test_json = {
        "characters": [
            {"key": "TravelerHydro", "level": 70},
            {"key": "TravelerPyro", "level": 69},
        ]
    }
    assert _convert_GOOD_to_list(test_json) == ["Traveler"]

    test_json = {
        "characters": [
            {"key": "KamisatoAyaka", "level": 70},
            {"key": "KaedeharaKazuha", "level": 69},
            {"key": "KukiShinobu", "level": 70},
        ]
    }
    expected_result = ["Kamisato Ayaka", "Kuki Shinobu"]
    assert _convert_GOOD_to_list(test_json) == expected_result
